import React, { useEffect, useState } from 'react'
import { useNavigate } from "react-router-dom";
import jwt_decode from "jwt-decode";
import Popup from 'reactjs-popup';
import './Home.css'

const Home = () => {
	const navigate = useNavigate();
	const [authenticated, setAuthenticated] = useState(false)
	const [books, setBooks] = useState({})
	const [tempBook, setTempBook] = useState('')

	async function populateBooks() {
		const req = await fetch('http://localhost:1337/api/books', {
			headers: {
				'x-access-token': localStorage.getItem('token')
			}
		})

		const data = await req.json()
		
		if (data.status === 'ok') {
			setBooks(data.books)
		}
		else {
			alert(data.error)
		}
	}

    useEffect(() => {
		const token = localStorage.getItem('token')

		if (token) {
			const user = jwt_decode(token)
			console.log(user)
			
			if (!user) {
				navigate('/login')
				localStorage.removeItem('token')
			}
			else {
				setAuthenticated(true)
				populateBooks()
			}
		}
		else {
			localStorage.removeItem('token')
			navigate('/login')
		}
	}, [navigate])

	function logoutUser() {
		localStorage.removeItem('token')
		setAuthenticated(false)
		window.location.href ='/login'
	}

	async function updateBooks(event) {
		event.preventDefault()

		if (!tempBook) {
			alert("Please type in a name for a book.")
			return
		}

		if (books.has(tempBook)) {
			alert("This book already exists in your list!")
			return
		}

		const req = await fetch('http://localhost:1337/api/books', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
				'x-access-token': localStorage.getItem('token')
			},
			body: JSON.stringify({
				book: tempBook,
			}),
		})

		const data = await req.json()
		
		if (data.status === 'ok') {
			setBooks(data.books)
			setTempBook('')
		}
		else {
			alert(data.error)
		}
	}

	async function removeBook(event, bookName) {
		event.preventDefault()

		const req = await fetch('http://localhost:1337/api/removebook', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
				'x-access-token': localStorage.getItem('token')
			},
			body: JSON.stringify({
				book: bookName,
			}),
		})

		const data = await req.json()
		
		if (data.status === 'ok') {
			setBooks(data.books)
		}
		else {
			alert(data.error)
		}
	}

	if(authenticated) {
		return (
			<div>
				{/* <ul>
					{([...books.keys()] || []).map(item => (
						<Popup trigger={<li key={item}>{item}</li>} modal nested>
							{close => (
