import React, { useEffect, useState } from 'react'
import { useNavigate } from "react-router-dom";
import jwt_decode from "jwt-decode";
import Popup from 'reactjs-popup';

const Home = () => {
	const navigate = useNavigate();
	const [authenticated, setAuthenticated] = useState(false)
	const [books, setBooks] = useState('')
	const [tempBook, setTempBook] = useState('')

	async function populateBooks() {
		const req = await fetch('http://localhost:1337/api/books', {
			headers: {
				'x-access-token': localStorage.getItem('token')
			}
		})

		const data = await req.json()
		
		if (data.status === 'ok') {
			setBooks(data.books)
		}
		else {
			alert(data.error)
		}
	}

    useEffect(() => {
		const token = localStorage.getItem('token')

		if (token) {
			const user = jwt_decode(token)
			console.log(user)
			
			if (!user) {
				navigate('/login')
				localStorage.removeItem('token')
			}
			else {
				setAuthenticated(true)
				populateBooks()
			}
		}
		else {
			localStorage.removeItem('token')
			navigate('/login')
		}
	}, [navigate])

	function logoutUser() {
		localStorage.removeItem('token')
		setAuthenticated(false)
		window.location.href ='/login'
	}

	async function updateBooks(event) {
		event.preventDefault()

		const req = await fetch('http://localhost:1337/api/books', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
				'x-access-token': localStorage.getItem('token')
			},
			body: JSON.stringify({
				book: tempBook,
			}),
		})

		const data = await req.json()
		
		if (data.status === 'ok') {
			setBooks(books => [...books, tempBook])
			setTempBook('')
		}
		else {
			alert(data.error)
		}
	}

	if(authenticated) {
		return (
			<div>
				<ul>
					{(books || []).map(item => (
						<li key={item}>{item}</li>
					))}
				</ul>
				<form onSubmit={updateBooks}>
					<input type="text" placeholder='Book name' onChange={(e) => setTempBook(e.target.value)} /> <br />
					<input type="submit" value="Add Book" />
				</form>
				<button onClick={logoutUser}>Logout</button>
				<Popup trigger={<button> Trigger</button>} position="right center">
					<div>Popup content here !!</div>
				</Popup>
			</div>	
		);
	}
}

export default Home
